import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';
import './App.css';

function App() {
  const [messages, setMessages] = useState([
    { text: "Good evening. How may I assist you, Master Wayne?", sender: "Alfred" },
  ]);
  const [input, setInput] = useState("");
  const messagesEndRef = useRef(null);

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  const handleSendMessage = async () => {
    if (input.trim()) {
      const userMessage = { text: input, sender: "User" };
      setMessages([...messages, userMessage]);
      setInput("");

      try {
        // Send the user's message to the backend
        const response = await axios.post('http://localhost:5000/chat', { message: input });
        const alfredResponse = { text: response.data.response, sender: "Alfred" };

        // Add Alfred's response to the chat
        setMessages((prevMessages) => [...prevMessages, userMessage, alfredResponse]);
      } catch (error) {
        console.error("Error communicating with Alfred's server:", error);
      }
    }
  };

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  };

  return (
    <div className="chat-container">
      <div className="chat-header">Alfred - Your Personal Butler</div>
      <div className="chat-body">
        {messages.map((message, index) => (
          <div key={index} className={`chat-message ${message.sender === 'Alfred' ? 'alfred' : 'user'}`}>
            <div className="message-sender">{message.sender}</div>
            <div className="message-text">{message.text}</div>
          </div>
        ))}
        <div ref={messagesEndRef} />
      </div>
      <div className="chat-footer">
        <input
          type="text"
          placeholder="Type a message..."
          value={input}
          onChange={(e) => setInput(e.target.value)}
          onKeyDown={(e) => e.key === 'Enter' && handleSendMessage()}
        />
        <button onClick={handleSendMessage}>Send</button>
      </div>
    </div>
  );
}

export default App;
